css:
:root {
  font-family: system-ui, sans-serif;
}

body {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  background: #f5f7fa;
  color: #333;
}

h1 {
  margin-bottom: 1rem;
}

/* 3 × 3 grid for the board */
#board {
  display: grid;
  grid-template-columns: repeat(3, 100px);
  grid-template-rows: repeat(3, 100px);
  gap: 5px;
}

.cell {
  width: 100px;
  height: 100px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.6rem;
  background: #fff;
  border: 2px solid #333;
  cursor: pointer;
  user-select: none;
}

#status {
  margin-top: 1rem;
  min-height: 1.5rem;
  font-weight: 600;
}

button {
  margin-top: 1rem;
  padding: 0.5rem 1.2rem;
  font-size: 1rem;
  border: none;
  border-radius: 6px;
  box-shadow: 0 2px 4px #0002;
  cursor: pointer;
  background: #4f8cff;
  color: #fff;
}

button:hover {
  opacity: 0.9;
}

javascript:
const boardEl = document.getElementById("board");
const statusEl = document.getElementById("status");
const resetBtn = document.getElementById("reset");

let board, current, playing;

const winSets = [
  [0, 1, 2],
  [3, 4, 5],
  [6, 7, 8], // rows
  [0, 3, 6],
  [1, 4, 7],
  [2, 5, 8], // columns
  [0, 4, 8],
  [2, 4, 6], // diagonals
];

function checkWin(p) {
  return winSets.some((set) => set.every((i) => board[i] === p));
}

function draw() {
  boardEl.innerHTML = "";
  board.forEach((mark, i) => {
    const cell = document.createElement("div");
    cell.className = "cell";
    cell.textContent = mark ?? "";
    cell.addEventListener("click", () => move(i));
    boardEl.appendChild(cell);
  });
}

function move(i) {
  if (!playing || board[i]) return;

  board[i] = current;

  if (checkWin(current)) {
    statusEl.textContent = `Player ${current} wins!`;
    playing = false;
  } else if (board.every(Boolean)) {
    statusEl.textContent = "It's a draw!";
    playing = false;
  } else {
    current = current === "X" ? "O" : "X";
    statusEl.textContent = `Player ${current}'s turn`;
  }
  draw();
}

function restart() {
  board = Array(9).fill(null);
  current = "X";
  playing = true;
  statusEl.textContent = `Player ${current}'s turn`;
  draw();
}

resetBtn.addEventListener("click", restart);
restart(); // initialize on load
